DROP TABLE IF EXISTS dg_coupon
CREATE TABLE `dg_coupon` (  `cid` int(11) NOT NULL auto_increment,  `sn` varchar(50) NOT NULL,  `uid` int(11) default NULL,  `uname` varchar(50) NOT NULL,  `getway` tinyint(3) default '1',  `endtime` int(11) default '0',  `addtime` int(11) default NULL,  `money` int(5) default '0',  `sellmoney` int(5) default NULL,  `state` tinyint(3) default '1',  PRIMARY KEY  (`cid`)) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_delivery
CREATE TABLE `dg_delivery` (  `did` int(11) NOT NULL auto_increment,  `areaid` int(11) NOT NULL,  `areaname` varchar(50) default NULL,  `serverfee` float(10,2) default NULL,  `deliveryname` varchar(50) NOT NULL,  `senddate` varchar(50) default NULL,  `queryurl` varchar(255) default NULL,  `first_weight` float(10,2) default NULL,  `continue_weight` float(10,2) default NULL,  `first_fee` float(10,2) default NULL,  `continue_fee` float(10,2) default NULL,  `fuel_fee` float(10,2) default '0.00',  `customs_fee` float(10,2) default '0.00',  `state` smallint(5) default '1',  PRIMARY KEY  (`did`)) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_favorite
CREATE TABLE `dg_favorite` (  `fid` int(11) NOT NULL auto_increment,  `uid` int(11) default NULL,  `uname` varchar(50) NOT NULL,  `goodsurl` varchar(255) NOT NULL,  `goodsname` varchar(255) NOT NULL,  `goodsprice` float(10,2) NOT NULL,  `goodsimg` varchar(255) default NULL,  `goodsseller` varchar(50) default NULL,  `sellerurl` varchar(255) default NULL,  `goodssite` varchar(50) default NULL,  `siteurl` varchar(255) default NULL,  `addtime` int(11) default NULL,  PRIMARY KEY  (`fid`)) ENGINE=MyISAM AUTO_INCREMENT=51 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_goods
CREATE TABLE `dg_goods` (  `gid` int(11) NOT NULL auto_increment,  `gtypeid` int(11) default NULL,  `goodsurl` varchar(255) default NULL,  `goodsname` varchar(255) default NULL,  `goodsprice` float(10,2) default NULL,  `goodsseller` varchar(50) default NULL,  `goodsimg` varchar(255) default NULL,  `sellerurl` varchar(255) default NULL,  `shopname` varchar(50) default NULL,  `rindex` tinyint(3) default '3',  `views` int(11) default NULL,  `buynum` int(11) default NULL,  `why` text,  `about` text,  `listorder` int(11) default '50',  `flag` char(2) default NULL,  `addtime` int(11) default NULL,  PRIMARY KEY  (`gid`)) ENGINE=MyISAM AUTO_INCREMENT=28 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_goodstype
