DROP TABLE IF EXISTS dg_guestbook
CREATE TABLE `dg_guestbook` (  `gid` int(11) NOT NULL auto_increment,  `uid` int(11) NOT NULL default '0',  `uname` varchar(255) default '',  `addtime` int(10) default '0',  `msg` mediumtext,  `reply` mediumtext,  `state` smallint(5) default '1',  `hide` smallint(5) default '0',  PRIMARY KEY  (`gid`)) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_news
CREATE TABLE `dg_news` (  `nid` int(11) NOT NULL auto_increment,  `title` varchar(255) default '',  `listorder` smallint(5) default '50',  `seokeywords` varchar(255) default '',  `seodescription` varchar(255) default '',  `body` text,  `addtime` int(11) default NULL,  PRIMARY KEY  (`nid`)) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_order
CREATE TABLE `dg_order` (  `oid` int(11) NOT NULL auto_increment,  `uid` int(11) NOT NULL,  `uname` varchar(50) NOT NULL,  `sid` varchar(50) default NULL COMMENT '货单发ID',  `typeid` smallint(5) default '12',  `goodsurl` varchar(255) NOT NULL,  `goodsname` varchar(255) NOT NULL,  `goodsprice` float(10,2) NOT NULL default '0.00',  `sendprice` float(10,2) NOT NULL default '0.00',  `goodsnum` int(10) default NULL,  `goodsimg` varchar(255) default NULL,  `goodssize` varchar(20) default NULL,  `goodscolor` varchar(20) default NULL,  `goodsseller` varchar(50) default NULL,  `sellerurl` varchar(255) default NULL,  `goodssite` varchar(50) default NULL,  `siteurl` varchar(255) default NULL,  `expressno` varchar(50) default '',  `type` smallint(5) default '0',  `goodsremark` varchar(255) default NULL,  `orderweight` int(11) default '0',  `orderremark` varchar(255) default NULL,  `orderimg` varchar(255) default NULL,  `payid` varchar(50) default NULL,  `paytime` int(11) default NULL,  `addtime` int(11) default NULL,  `uptime` int(11) default NULL,  `state` smallint(5) default '1',  PRIMARY KEY  (`oid`)) ENGINE=MyISAM AUTO_INCREMENT=24 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_otype
CREATE TABLE `dg_otype` (  `typeid` int(11) NOT NULL auto_increment,  `typename` varchar(50) default NULL,  `node` int(11) default NULL,  `listorder` int(11) default NULL,  PRIMARY KEY  (`typeid`)) ENGINE=MyISAM AUTO_INCREMENT=13 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_payid
