DROP TABLE IF EXISTS dg_record
CREATE TABLE `dg_record` (  `rid` int(11) NOT NULL auto_increment,  `uid` int(11) NOT NULL,  `uname` varchar(50) default NULL,  `type` smallint(2) default '1',  `action` smallint(5) default '0',  `money` float(10,2) default NULL,  `accountmoney` float(10,2) default NULL,  `remark` varchar(255) default NULL,  `addtime` int(11) default NULL,  PRIMARY KEY  (`rid`)) ENGINE=MyISAM AUTO_INCREMENT=35 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_refund
CREATE TABLE `dg_refund` (  `rid` int(11) NOT NULL auto_increment,  `rechargeid` int(11) default NULL,  `uid` int(11) default NULL,  `uname` varchar(50) NOT NULL,  `money` float(10,2) default NULL,  `rechargetime` int(11) default NULL,  `rechargemoney` float(10,2) default NULL,  `rechargesn` varchar(50) default NULL,  `remark` varchar(255) default NULL,  `addtime` int(11) default NULL,  `state` smallint(5) default '1',  PRIMARY KEY  (`rid`)) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_scorerecord
CREATE TABLE `dg_scorerecord` (  `sid` int(11) NOT NULL auto_increment,  `uid` int(11) NOT NULL,  `uname` varchar(50) default '',  `remark` varchar(255) default '',  `score` int(11) default '0',  `totalscore` int(11) default NULL,  `type` smallint(5) default '1' COMMENT '1获得2消费',  `addtime` int(11) default NULL,  PRIMARY KEY  (`sid`)) ENGINE=MyISAM AUTO_INCREMENT=11 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_sendorder
CREATE TABLE `dg_sendorder` (  `sid` int(11) NOT NULL auto_increment,  `sn` varchar(50) default NULL,  `uid` int(11) default NULL,  `uname` varchar(50) NOT NULL,  `email` varchar(255) default NULL,  `oids` varchar(255) default NULL,  `couponid` int(11) default NULL,  `freight` float(10,2) NOT NULL default '0.00',  `serverfee` float(10,2) default '0.00',  `customsfee` float(10,2) default '0.00' COMMENT '报关费',  `totalfee` float(10,2) default '0.00',  `countmoney` float(10,2) default NULL,  `countweight` float(10,2) default NULL,  `consignee` varchar(30) NOT NULL,  `country` varchar(30) default NULL,  `city` varchar(30) default NULL,  `zip` varchar(30) default NULL,  `tel` varchar(30) default NULL,  `address` varchar(255) default NULL,  `remark` varchar(255) default NULL,  `did` int(11) default NULL,  `deliveryname` varchar(30) default NULL,  `areaname` varchar(30) default NULL,  `addtime` int(11) default NULL,  `uptime` int(11) default NULL,  `comment` text,  `commenttime` int(11) default NULL,  `reply` text,  `showcomment` smallint(5) default '0',  `state` smallint(5) default '1',  PRIMARY KEY  (`sid`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_shopsite
CREATE TABLE `dg_shopsite` (  `sid` int(11) NOT NULL auto_increment,  `shopname` varchar(50) NOT NULL default '淘宝网',  `shopurl` varchar(50) NOT NULL default 'www.taobao.com',  `shopcode` varchar(20) NOT NULL default 'taobao',  `charset` varchar(10) NOT NULL default 'gbk',  `preg_goodsname` varchar(255) default NULL,  `preg_goodsname2` varchar(255) default NULL,  `preg_goodsname3` varchar(255) default NULL,  `preg_goodsprice` varchar(255) default NULL,  `preg_goodsprice2` varchar(255) default NULL,  `preg_goodsprice3` varchar(255) default NULL,  `preg_sendprice` varchar(255) default NULL,  `preg_sendprice2` varchar(255) default NULL,  `preg_sendprice3` varchar(255) default NULL,  `preg_goodsimg` varchar(255) default NULL,  `preg_goodsimg2` varchar(255) default NULL,  `preg_goodsimg3` varchar(255) default NULL,  `preg_goodsseller` varchar(255) default NULL,  `preg_goodsseller2` varchar(255) default NULL,  `preg_goodsseller3` varchar(255) default NULL,  `preg_sellerurl` varchar(255) default NULL,  `preg_sellerurl2` varchar(255) default NULL,  `preg_sellerurl3` varchar(255) default NULL,  `state` smallint(5) default '1',  PRIMARY KEY  (`sid`)) ENGINE=MyISAM AUTO_INCREMENT=3 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_smtpaccount
CREATE TABLE `dg_smtpaccount` (  `eid` int(11) NOT NULL auto_increment,  `smtp_server` varchar(50) default '' COMMENT '邮件服务器地址',  `smtp_port` smallint(5) default '25' COMMENT '邮件服务器端口号',  `smtp_email` varchar(50) default NULL,  `smtp_account` varchar(50) default '' COMMENT '邮件服务器账户名',  `smtp_password` varchar(255) default '' COMMENT '邮件服务密码',  `smtp_name` varchar(255) default NULL COMMENT '发信人名字',  `reply_address` varchar(255) default '' COMMENT '回复地址',  `smtp_auth` smallint(5) default '1' COMMENT '是否smtp验证',  `smtp_ssl` smallint(5) default '0' COMMENT '否是ssl模式',  `state` smallint(5) default '1' COMMENT '状态1正常0禁用',  PRIMARY KEY  (`eid`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_special
CREATE TABLE `dg_special` (  `sid` int(11) NOT NULL auto_increment,  `title` varchar(255) default NULL,  `flag` char(2) default NULL,  `about` varchar(255) default NULL,  `pic` varchar(255) default NULL,  `seokeywords` varchar(255) default NULL,  `seodescription` varchar(255) default NULL,  `body` mediumtext,  `listorder` smallint(5) default '50',  `addtime` int(11) default NULL,  PRIMARY KEY  (`sid`)) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_sysconfig
CREATE TABLE `dg_sysconfig` (  `sid` int(11) NOT NULL auto_increment,  `varname` varchar(50) default '' COMMENT '变量名',  `info` varchar(255) default '' COMMENT '说明',  `groupid` smallint(5) default '0' COMMENT '分组，备用',  `type` varchar(20) default 'string' COMMENT 'string字符串number数字bool是否',  `value` mediumtext COMMENT '值',  PRIMARY KEY  (`sid`)) ENGINE=MyISAM AUTO_INCREMENT=34 DEFAULT CHARSET=utf8
DROP TABLE IF EXISTS dg_users
CREATE TABLE `dg_users` (  `uid` int(11) NOT NULL auto_increment,  `uname` varchar(50) NOT NULL,  `password` char(32) NOT NULL,  `email` char(50) default NULL,  `tname` varchar(50) default NULL,  `utype` smallint(5) default '0',  `sex` smallint(5) default '0',  `tel` varchar(50) default NULL,  `zip` varchar(20) default NULL,  `address` varchar(255) default NULL,  `qq` varchar(20) default '',  `msn` varchar(50) default '',  `country` varchar(50) default NULL,  `city` varchar(50) default NULL,  `face` varchar(50) default NULL,  `scores` int(11) default '0',  `money` decimal(10,2) default '0.00' COMMENT '账户余额',  `regip` char(16) default NULL,  `regtime` int(11) default NULL,  `loginip` char(16) default NULL,  `logintime` int(11) default NULL,  `activekey` char(5) default NULL,  `state` smallint(5) default '1',  PRIMARY KEY  (`uid`)) ENGINE=MyISAM AUTO_INCREMENT=8 DEFAULT CHARSET=utf8
